$(document).ready(function(){
	var size = prompt('Enter Your Board Size:');
	function CheckSize(){
		if(size == ''){
		size = prompt('You didnt enter a size!\n Enter one now:');
		};
		if(size >= 151){
		size = prompt('Your size is too big!\n Enter a new size:');
		};
	};
	CheckSize();
	var ColorSelected = 'highlighted'
	var GlowVerify = false
	//Creates Grid depending on Size inputed
	function CreateGrid(){
		for(var i=1;i<= size; i++){
		$('.columns').append('<div class = box></div>')
	};
		for(var i=1;i<= size; i++){
		$('.box').append('<div class = inline></div>')
		$('.box').addClass('boxd');
	};
	}
	CreateGrid();
	//Highlights individual grid objects on mouse enter
	function MouseSet(){
		$('.inline').mouseenter('.inline',function(){
		$(this).removeClass('highlighted');
		$(this).removeClass('highlightedred');
		$(this).removeClass('highlightedgreen');
		$(this).removeClass('highlightedpurple');
		$(this).removeClass('highlightedrandom');
		$(this).removeClass('glowblue');
		$(this).removeClass('glowpink');
		$(this).removeClass('glowgreen');
		$(this).removeClass('glowpurple');
		$(this).removeClass('glowyellow');
		$(this).removeClass('eraser');
		$(this).addClass(ColorSelected);
	});
	}
	MouseSet();
	//For the clear button, Only resets Mouse function
	function ClearGrid(){
		$('.inline').removeClass('highlighted');
		$('.inline').removeClass('highlightedred');
		$('.inline').removeClass('highlightedgreen');
		$('.inline').removeClass('highlightedpurple');
		$('.inline').removeClass('highlightedrandom');
		$('.inline').removeClass('glowblue');
		$('.inline').removeClass('glowpink');
		$('.inline').removeClass('glowgreen');
		$('.inline').removeClass('glowpurple');
		$('.inline').removeClass('glowyellow');
	}
	$('#clear').click(function(){
		ClearGrid();
	})
	//When the Size button is pressed, the grid and mouse function
	//will be reset
	$('#size').click(function(){
		size = prompt('Change Size:');
		CheckSize();
		$('.inline').remove();
		$('.box').remove()
		CreateGrid();
		MouseSet();
		if(GlowVerify == true){
			$('body').addClass('nightcall');
			$('.box').addClass('nightcallb');
		}
		else{
			$('body').removeClass('nightcall');
			$('.box').removeClass('nightcallb');
		}
	});
	$('#colorbutton').click(function(){
		$('.modes').hide();
		$('.color').fadeIn(200);
	})

	function ColorSet(){
		//$('.inline').removeClass(ColorSelected);
		MouseSet();
	}
	function GlowSet(){
		ClearGrid();
		ColorSelected = 'glowblue'
		ColorSet();
		$('.modes').hide();
		$('.color').hide();
		$('.neon').fadeIn(200);
		$('#colorbutton').click(function(){
		$('.modes').hide();
		$('.color').hide();
		$('.neon').fadeIn(200);
		});
		$('#mode').click(function(){
		$('.color').hide();
		$('.neon').hide();
		$('.modes').fadeIn(200);
	});
		$('.neongreen').click(function(){
		ColorSelected = 'glowgreen'
		ColorSet();

	});
		$('.neonblue').click(function(){
		ColorSelected = 'glowblue'
		ColorSet();
	});
		$('.neonpink').click(function(){
		ColorSelected = 'glowpink'
		ColorSet();

	});
		$('.neonpurple').click(function(){
		ColorSelected = 'glowpurple'
		ColorSet();
	});
		$('.neonyellow').click(function(){
		ColorSelected = 'glowyellow'
		ColorSet();
	});
	}


	$('.green').click(function(){
		ColorSelected = 'highlightedgreen'
		ColorSet();

	});
	$('.blue').click(function(){
		ColorSelected = 'highlighted'
		ColorSet();
	});
	$('.purple').click(function(){
		ColorSelected = 'highlightedpurple'
		ColorSet();
	});
	$('.random').click(function(){
		ColorSelected = 'highlightedrandom'
		ColorSet();
	});
	$('.red').click(function(){
		ColorSelected = 'highlightedred'
		ColorSet();
	});
	$('#mode').click(function(){
		$('.color').hide();
		$('.modes').fadeIn(200);
	})
	$('.glow').click(function(){
		$('body').addClass('nightcall');
		$('.box').addClass('nightcallb');
		GlowSet();
		GlowVerify = true
		//$('#size').addClass('glowing');
	})
	$('.normal').click(function(){
		$('body').removeClass('nightcall');
		$('.box').removeClass('nightcallb');
		ClearGrid();
		ColorSelected = 'highlighted'
		ColorSet();
		GlowVerify = false
		$('.modes').hide();
		$('.color').fadeIn(200);
		$('.neon').hide();
		$('#colorbutton').click(function(){
		$('.modes').hide();
		$('.color').fadeIn(200);
		$('.neon').hide();
		});
		$('#mode').click(function(){
		$('.color').hide();
		$('.neon').hide();
		$('.modes').fadeIn(200);
	});
	})
	$('#eraser').click(function(){
		ColorSelected = 'eraser'
		ColorSet();
	})
});