$(document).ready(function(){
	for(var i=1;i<= 16; i++){
		$('.columns').append('<div class = box></div>')
	};
	for(var i=1;i<= 16; i++){
		$('.box').append('<div class = inline></div>')
	};
	$('.inline').mouseenter('.inline',function(){
		$(this).css({'background-color': 'blue'})
	});
});